INSERT INTO orderStatus (name, description) VALUES ('Pending payment', 'Order received, no payment initiated. Awaiting payment (unpaid).'), ('Failed', 'Payment failed or was declined (unpaid) or requires authentication (SCA). Note that this status may not show immediately and instead show as Pending until verified (e.g., PayPal).'), ('Processing', 'Payment received (paid) and stock has been reduced; order is awaiting fulfillment. All product orders require processing, except those that only contain products which are both Virtual and Downloadable.'), ('Completed', 'Order fulfilled and complete and is now in delivering process. Requires no further action.'), ('On hold', 'Awaiting payment. Stock is reduced, but you need to confirm payment.'), ('Canceled', 'Canceled by an admin or the customer. Stock is increased, no further action required.'),('Refunded', 'Refunded by an admin. No further action required.');
INSERT INTO orderProduct (name, description, quantityLeft, price) VALUES ('Computer', 'A device that accepts information (in the form of digitalized data) and manipulates it for some result based on a program, software, or sequence of instructions on how the data is to be processed.', 200, 2000.99), ('Mobile phone', 'A wireless handheld device that allows users to make and receive calls. While the earliest generation of mobile phones could only make and receive calls, today''s mobile phones do a lot more, accommodating web browsers, games, cameras, video players and navigational systems.', 56, 500.00), ('Camera', 'Device for recording an image of an object on a light-sensitive surface; it is essentially a light-tight box with an aperture to admit light focused onto a sensitized film or plate.', 42, 238.59), ('Washing machine', 'A home appliance used to wash laundry. The term is mostly applied to machines that use water as opposed to dry cleaning (which uses alternative cleaning fluids and is performed by specialist businesses) or ultrasonic cleaners.', 11, 199.99), ('Car', 'A vehicle that has wheels, carries a small number of passengers, and is moved by an engine or a motor. Cars are also called automobiles or motor vehicles. Trucks and buses are motor vehicles as well.', 128, 10000.00);
